mapper.delete:
DELETE 
FROM code_detail
WHERE group_code = #{groupCode}
AND code_value =#{codeValue}

mapper.update:
UPDATE code_detail
SET code_name =#{codeName}
WHERE group_code = #{groupCode} 
AND code_value = #{codeValue}

mapper.select:
SELECT IFNULL(MAX(sort_seq),0)
FROM code_detail
WHERE groud_code = #{groupCode}
